function animateTopControl(){window.pageYOffset>=250?addClass(topControl,"active"):removeClass(topControl,"active")}function scrollTo(o,l,n){if(!(0>n)){var t=l-o.scrollTop,c=t/n*2;setTimeout(function(){o.scrollTop=o.scrollTop+c,scrollTo(o,l,n-2)},10)}}function addClass(o,l){o.className+=" "+l}function removeClass(o,l){for(var n=" "+o.className+" ";-1!=n.indexOf(" "+l+" ");)n=n.replace(" "+l+" ","");o.className=n}window.onscroll=animateTopControl;var topControl=document.getElementById("top-control");topControl.onclick=function(){scrollTo(document.body,0,100)};